import { Link, useParams } from "react-router-dom"
import styles from "../components/layout/Pesquisa.module.css"
import { Button } from "@mui/material"
import styleButton from "../components/layout/StyleButton.module.css"
import styleBotaoCheck from "../components/layout/BotaoCheck.module.css"
import { useLocation } from "react-router-dom"

function renderCondition(valor){
    if (valor == "Nome"){
        return (
            <form>
                <input name="name" autoComplete="off" required class = {styles.input}/>
                <label class = {styles.label} for="name"> 
                    <span class = {styles.span}>Name</span>
                </label>
            </form>
        )
    }
    if (valor == "Cpf"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Cargo"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Salario"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Email"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Sexo"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Nascimento"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
    if (valor == "Registro"){
        return (
            <div>
                <h3 class = {styles.h3}>{valor}</h3>
                <input type="text" class = {styles.input}></input>
            </div>
        )
    }
}

function Pesquisar() {
    const location = useLocation();
    const opcao = location.state[0];
    const vec = location.state;

    return (
        <buscar className="App">
            <header className="App-header">
                Pesquisar
            </header>
            <br></br>
            <div className={styles.menu}>
                {vec.map((valor) => 
                        renderCondition(valor)
                    )
                }
            </div>

            {opcao == "excluir" && <div class={styles.botoes}><button className={styleButton.button}>Pesquisar</button></div>}
            {opcao == "editar" && <div class={styles.botoes}><button className={styleButton.button}>Pesquisar</button></div>}
            {opcao == "buscar" && <div class={styles.botoes}><button className={styleButton.button}>Pesquisar</button></div>}
            {opcao == "analisar" && <div class={styles.botoes}><button className={styleButton.button}>Pesquisar</button></div>}
            <div class={styles.botoes}><Button class={styleButton.button} href="/menu" size="small">Voltar</Button></div>
        </buscar>
    )
}

export default Pesquisar
